import { TAREAS_PROYECTO, AGREGAR_TAREA, ERROR_TAREA, ELIMINAR_TAREA, ESTADO_TAREA } from '../../types';

export default (state, action) => {
    switch (action.type) {
        case TAREAS_PROYECTO:
            return {
                ...state,
                tareasProyecto: state.tareas.filter(tarea => tarea.proyectoId === action.payload)
            }
        case AGREGAR_TAREA:
            return {
                ...state,
                tareas: [action.payload, ...state.tareas],
                errorTarea: false
            }
        case ERROR_TAREA:
            return {
                ...state,
                errorTarea: true
            }
        case ELIMINAR_TAREA:
            return {
                ...state,
                tareas: state.tareas.filter(tarea => tarea.id !== action.payload)
            }
        case ESTADO_TAREA:
            return {
                ...state,
                tareas: tareasProyecto.tareas.map(tarea => tarea.id === action.payload.id ? action.payload : tarea)
            }

        default: return state;
    }
}
